using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

public class Script
{
    public bool Execute(string[] args, string clipboardText, out string resultText)
    {
        StringBuilder sb = new StringBuilder();
        sb.AppendLine( string.Format( "-- dump the row count for all tables in the database", args ));
        sb.AppendLine( string.Format( "CREATE TABLE #counts", args ));
        sb.AppendLine( string.Format( "(", args ));
        sb.AppendLine( string.Format( "    table_name varchar(255 ),", args ));
        sb.AppendLine( string.Format( "    row_count int", args ));
        sb.AppendLine( string.Format( ")", args ));
        sb.AppendLine( string.Format( "", args ));
        sb.AppendLine( string.Format( "EXEC sp_MSForEachTable @command1= 'INSERT #counts (table_name, row_count) SELECT ''?'', COUNT(*) FROM ?'", args ));
        sb.AppendLine( string.Format( "SELECT table_name, row_count FROM #counts ORDER BY row_count DESC", args ));
        resultText = sb.ToString();
        return true;
    }
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

public class Script
{
    public bool Execute(string[] args, string clipboardText, out string resultText)
    {
        StringBuilder sb = new StringBuilder();
        sb.AppendLine( string.Format( "-- List all tables with a given column name", args ));
        sb.AppendLine( string.Format( "SELECT COLUMN_NAME, TABLE_NAME", args ));
        sb.AppendLine( string.Format( "FROM INFORMATION_SCHEMA .COLUMNS", args ));
        sb.AppendLine( string.Format( "WHERE COLUMN_NAME LIKE '%{1}%'", args ));
        resultText = sb.ToString();
        return true;
    }
}
