using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

public class Script
{
    public bool Execute(string[] args, string clipboardText, out string resultText)
    {
        StringBuilder sb = new StringBuilder();
        string[] words = clipboardText.Split(' ');
        foreach (string word in words)
        {
            string newWord = word.Trim();
            if (newWord.Length > 0)
            {
                string upper = newWord.ToUpper();
                newWord = newWord.ToLower();
                newWord = upper[0] + newWord.Substring(1);
                sb.Append(newWord);
                sb.Append(' ');
            }
        }
        resultText = sb.ToString();
        return true;
    }
}
